package com.hsicen.core.nowcoderimport java.util.Scanner/** * @author: hsc * @date: 2023/12/8 17:42 * @email: codinghuang@163.com * description: HJ6 质数因子 * * * 功能:输入一个正整数，按照从小到大的顺序输出它的所有质因子（重复的也要列举）（如180的质因子为2 2 3 3 5 ） */fun main() {    val br = Scanner(System.`in`)    var num = br.nextLine()?.toInt() ?: 0    var i = 2    while (i * i <= num) {        if (num % i == 0) {            print("$i ")            num /= i            i--        }        i++    }    println("$num ")}