package com.hsicen.code.nowcoderimport java.util.Scanner/** * @author: hsicen * @date: 2023/12/12 15:35 * @email: codinghuang@163.com * description: HJ7 取近似值 * * 写出一个程序，接受一个正浮点数值，输出该数值的近似整数值。 * 如果小数点后数值大于等于 0.5 ,向上取整；小于 0.5 ，则向下取整。 */fun main() {    val sc = Scanner(System.`in`)    val num = sc.nextLine()?.toFloat() ?: 0f    if (num % 1 >= 0.5) {        println(num.toInt() + 1)    } else {        println(num.toInt())    }}