package com.hsicen.core.nowcoderimport java.util.Scanner/** * @author: hsc * @date: 2023/12/6 18:02 * @email: codinghuang@163.com * description: HJ5 进制转换 * * 描述：写出一个程序，接受一个十六进制的数，输出该数值的十进制表示。 */fun main() {  val sc = Scanner(System.`in`)  while (sc.hasNext()) {    println(hexToInt(sc.next()))  }}private fun hexToInt(src: String?): Int {  val content = src?.substring(2)?.toCharArray() ?: return 0  if (content.isEmpty()) return 0  val len = content.size - 1  for (i in len downTo 0) {    println("第${i}个字符为: ${content[i]}")  }  return 1}