package com.hsicen.core.nowcoderimport java.util.Scanner/** * @author: hsicen * @date: 2023/12/12 16:10 * @email: codinghuang@163.com * description: HJ9 提取不重复的整数 * * 输入一个 int 型整数，按照从右向左的阅读顺序，返回一个不含重复数字的新的整数。 * 保证输入的整数最后一位不是 0 。 */fun main() {  val sc = Scanner(System.`in`)  val num = sc.nextInt()  var base = 10  val flag = BooleanArray(10)  val sb = StringBuilder()  while (num / base != 0) {    val i = (num % base) / (base / 10)    if (flag[i].not()) {      sb.append(i)      flag[i] = true    }    base *= 10  }  val i = (num % base) / (base / 10)  if (flag[i].not()) {    sb.append(i)    flag[i] = true  }  println(sb.toString())}